script:
frappe.ui.form.on('havano_salary_component', {
    component_mode: function(frm) {
        if (frm.doc.component_mode === 'Medical Aid') {
            // Show the section
            frm.set_df_property('section_break_mgmx', 'hidden', false);
        } else {
            // Hide the section
            frm.set_df_property('section_break_mgmx', 'hidden', true);

            // Optional: clear values inside the section
            frm.set_value('employee_amount', '');
            frm.set_value('employer_amount', '');
        }
    },
    refresh: function(frm) {
        // Trigger logic on form load
        frm.trigger('component_mode');
    }
});

frappe.ui.form.on('havano_salary_structure', {
    setup: function(frm) {
        // Helper function to set up filters for both tables
        function set_component_filter(child_table_name, component_type) {
            frm.fields_dict[child_table_name].grid.get_field('components').get_query = function(doc, cdt, cdn) {
                return {
                    filters: [
                        ['type', '=', component_type],
                        ['company', '=', frm.doc.company],
                        // Nested child table condition:
                        // Only include components whose linked "account" child table has matching company
                        ['havano_salary_accounts', 'company', '=', frm.doc.company]
                    ]
                };
            };
        }

        // ðŸ”¹ Apply for Earnings
        set_component_filter('earnings', 'Earning');

        // ðŸ”¹ Apply for Deductions
        set_component_filter('deductions', 'Deduction');
    }
});

frappe.ui.form.on('havano_employee', {
    setup: function(frm) {
        // Helper function to set up filters for both tables
        function set_component_filter(child_table_name, component_type) {
            frm.fields_dict[child_table_name].grid.get_field('components').get_query = function(doc, cdt, cdn) {
                return {
                    filters: [
                        ['type', '=', component_type],
                        ['company', '=', frm.doc.company],
                        // Nested child table condition:
                        // Only include components whose linked "account" child table has matching company
                        ['havano_salary_accounts', 'company', '=', frm.doc.company]
                    ]
                };
            };
        }

        // ðŸ”¹ Apply for Earnings
        set_component_filter('employee_earnings', 'Earning');

        // ðŸ”¹ Apply for Deductions
        set_component_filter('employee_deductions', 'Deduction');
    }
});

frappe.ui.form.on('havano_employee', {
    onload(frm) {
        toggle_sections(frm);
    },

    refresh(frm) {
        toggle_sections(frm);
    },

    payslip_type(frm) {
        toggle_sections(frm);
    }
});

function toggle_sections(frm) {
    const payslip_type = frm.doc.payslip_type;

    // Show these only for Base Currency
    const show_base = payslip_type === "Base Currency";
    frm.toggle_display('hidden_calculations_section', show_base);
    frm.toggle_display('net_income_section', show_base);

    // Show these only for Split Currency
    const show_split = payslip_type === "Split Currency";
    frm.toggle_display('all_net_incomes_section', show_split);
    frm.toggle_display('double_calculations_section', show_split);
}

frappe.ui.form.on('havano_employee', {
    refresh(frm) {
        if(!frm.is_new()) {
            frm.add_custom_button('Generate Payslip', () => {
                frappe.call({
                    method: "frappe.client.get",
                    args: {
                        doctype: frm.doc.doctype,
                        name: frm.doc.name
                    },
                    callback: function(r) {
                        const html = frappe.render_template("Havano Employee Payslip", { doc: r.message });
                        const w = window.open('', '_blank');
                        w.document.write(html);
                        w.document.close();
                        w.focus();
                        w.print();
                    }
                });
            });
        }
    }
});
